<?phpinclude('GlobalController.php');defined('BASEPATH') OR exit('No direct script access allowed');class Asignatura extends GlobalController{    public function __construct()    {        parent::__construct();        $this->load->model('Asignatura_model');        echo "valor ".parent::Saludos(7);exit;    }    public function CreateAsignatura()    {        if($_POST['BtnNewAsignatura'] == 1)        {            foreach ($_POST as $clave => $valor)            {                if($clave != 'BtnNewAsignatura')                {                    $array[$clave] = $valor;                }            }            $this->Asignatura_model->ModelInsertAsignatura($array);        }    }}