<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Editor extends CI_Controller {    public function __construct() {        parent::__construct();        $this->load->model("Editor/GestorSlideModel");    }    public function home() {        //echo 'Porfavor :) Aqui va el admin del editor';        $this->load->view('BackEnd/Editor/EditorPerfilView');    }    public function slider() {        // echo "Esto es la gestion del slider.";        // llamar al modelo para mostrar los Slide actuales        $datos['sliders'] = $this->GestorSlideModel->todosSlideModel();        $this->load->view('BackEnd/Editor/EditorSlideView', $datos);            }}